import React, { useState,useEffect } from 'react'
import './style.css'
const Temp = () => {

  const [searchValue, setSearchValue] = useState("pune");

  const getWeather = async()=>{
    try{
      let url = `https://api.openweathermap.org/data/2.5/weather?q=${searchValue}&units=metric&appid=479a7b690583815022ddc7c7a046ce0a`;

       let res= await fetch(url);
       let data= await res.json();

       const { temp} = data.main;
       console.log(temp)
    } catch (error) {
      console.log(error);
    }
  };

  useEffect(()=>{
    getWeather()
  },[]);
  return (
    <>
      <div className="search">
        <input type="search" placeholder='search...' autoFocus id='search' className='searchTerm' value={searchValue } onChange={(e)=>setSearchValue(e.target.value) }/>

        <button className='searchBtn' onClick={getWeather}>Search</button>
      </div>

      {/* temp card */}
      <article className='widget'>
        <div className="weatherIcon">
          <i className="wi wi-day-sunny"></i>
        </div>
        <div className="weather">
          <div className="weatherInfo">
            <div className="temperature">
              <span>25.5 &deg;</span>
            </div>
            <div className="description">
              <div className="weatherCondition">sunny</div>
              <div className="place">Pune, In</div>
            </div>
          </div>

            <div className='date'>{new Date().toLocaleString("en-US")}</div>
        </div>
        {/* bottom 4 section  */}
        <div className="extra-temp">
            <div className="two-sided-section">
              <p>
                <i className='wi wi-sunset'></i>
              </p>
              <p className="extra-info-leftside">19:19 pm <br />Sunset</p>
            </div>

            <div className="two-sided-section">
              <p>
                <i className='wi wi-rain'></i>
              </p>
              <p className="extra-info-leftside">19:19 pm <br />Rain</p>
            </div>

            <div className="two-sided-section">
              <p>
                <i className='wi wi-humidity'></i>
              </p>
              <p className="extra-info-leftside">19:19 pm <br />Humidity</p>
            </div>

            <div className="two-sided-section">
              <p>
                <i className='wi wi-strong-wind'></i>
              </p>
              <p className="extra-info-leftside">19:19 pm <br />Speed</p>
            </div>
        </div>
      </article>
    </>
  )
}

export default Temp
