import React, { useState } from 'react';
import "./style.css";
import Menu from "./menuApi.js";
import MenuCard from "./MenuCard";

const Restaurant = () => {
  
  const [menuData, setMenuData] = useState(Menu);

const filterItem = (category) =>{
  const updatedList = Menu.filter((curElem)=>{
    return curElem.category== category;
  })
}

  return (
    <>
    <nav className='navbar'>
<div className="btn-group">
  <button className='btn-group__item' onClick={filterItem("breakfast")}>Breakfast</button>
  <button className='btn-group__item' onClick={filterItem("lunch")}>Launch</button>
  <button className='btn-group__item' onClick={filterItem("evening")}>Evening</button>
  <button className='btn-group__item' onClick={filterItem("diner")}>Diner</button>
  <button className='btn-group__item' onClick={filterItem("all")}>All</button>
</div>
    </nav>
    <MenuCard menuDatas={menuData}/>
    </>
  )
}

export default Restaurant;
