import React from 'react';
import Restaurant from './restaurant';
import Menu from './menuApi'

const MenuCard = ({ menuDatas }) => {
    return (
        <>
            <section className=' main-card--container'>
                <div className='row'>
                    {
                        menuDatas.map((curElem) => {
                            const { id, category, name, description, image, price } = curElem;

                            return (
                                <>
                                    <div className='card-container col-4' key={id}>
                                        <div className='card'>
                                            <div className='card-body'>
                                                <span className='card-number card-circle subtle'>{id}</span>
                                                <span className='card-author subtle'>{category}</span>
                                                <h2 className='card-title'>{name}</h2>
                                                <span className='card-description subtle'>{description}</span>
                                                <div className='card-read'>Read More</div>
                                                <img src={image} class="card-media" alt="image" />
                                                <span className='card-tag subtle'>Order Now</span>

                                            </div>
                                        </div>
                                    </div>
                                </>
                            )

                        })
                    }</div>
            </section>
        </>
    )

}

export default MenuCard
